import React, { useState, useEffect } from "react";
import mqtt from "mqtt";
import "./App.css";

function App() {
  const [metrics, setMetrics] = useState(null);
  const [connected, setConnected] = useState(false);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Connect to MQTT broker via WebSocket
    const brokerUrl =
      process.env.REACT_APP_MQTT_BROKER || "ws://localhost:9001";
    const topic = process.env.REACT_APP_MQTT_TOPIC || "protexai/sensors";

    console.log("Connecting to MQTT broker:", brokerUrl);

    const client = mqtt.connect(brokerUrl, {
      clientId: `ui-consumer-${Math.random().toString(16).slice(2, 10)}`,
    });

    client.on("connect", () => {
      console.log("Connected to MQTT broker");
      setConnected(true);
      client.subscribe(topic, (err) => {
        if (err) {
          console.error("Subscription error:", err);
        } else {
          console.log("Subscribed to topic:", topic);
        }
      });
    });

    client.on("message", (topic, message) => {
      try {
        const data = JSON.parse(message.toString());
        setMetrics(data);
        setHistory((prev) => [data, ...prev].slice(0, 10)); // Keep last 10
      } catch (err) {
        console.error("Error parsing message:", err);
      }
    });

    client.on("error", (err) => {
      console.error("MQTT error:", err);
      setConnected(false);
    });

    client.on("close", () => {
      console.log("Connection closed");
      setConnected(false);
    });

    return () => {
      client.end();
    };
  }, []);

  const getStatusColor = (percent) => {
    if (percent > 80) return "#ff4444";
    if (percent > 60) return "#ffaa00";
    return "#44ff44";
  };

  const getTemperature = (temps) => {
    if (!temps || temps.length === 0) return null;

    // Find CPU temp (Tctl)
    for (const temp of temps) {
      if (temp.label && temp.label.includes("Tctl")) {
        return { type: "CPU", value: temp.current_c };
      }
    }

    // Find GPU temp (edge)
    for (const temp of temps) {
      if (temp.label && temp.label.includes("edge")) {
        return { type: "GPU", value: temp.current_c };
      }
    }

    // Find SSD temp (Composite)
    for (const temp of temps) {
      if (temp.label && temp.label.includes("Composite")) {
        return { type: "SSD", value: temp.current_c };
      }
    }

    return null;
  };

  if (!connected) {
    return (
      <div className="App">
        <div className="status-bar disconnected">
          <span className="status-dot"></span>
          Connecting to MQTT broker...
        </div>
      </div>
    );
  }

  if (!metrics) {
    return (
      <div className="App">
        <div className="status-bar connected">
          <span className="status-dot"></span>
          Connected - Waiting for metrics...
        </div>
      </div>
    );
  }

  const temp = getTemperature(metrics.temperature);

  return (
    <div className="App">
      <div className="status-bar connected">
        <span className="status-dot"></span>
        Connected to MQTT
      </div>

      <div className="container">
        <header className="header">
          <h1>ProtexAI System Monitor</h1>
          <p className="platform">{metrics.platform}</p>
        </header>

        <div className="metrics-grid">
          <div className="metric-card">
            <div className="metric-header">
              <h3>CPU</h3>
              <span
                className="metric-value"
                style={{ color: getStatusColor(metrics.cpu.usage_percent) }}
              >
                {metrics.cpu.usage_percent}%
              </span>
            </div>
            <div className="metric-details">
              {metrics.cpu.cores_physical && (
                <p>
                  Cores: {metrics.cpu.cores_physical}P /{" "}
                  {metrics.cpu.cores_logical}L
                </p>
              )}
              {metrics.cpu.frequency_mhz && (
                <p>Frequency: {metrics.cpu.frequency_mhz.toFixed(0)} MHz</p>
              )}
            </div>
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{
                  width: `${metrics.cpu.usage_percent}%`,
                  backgroundColor: getStatusColor(metrics.cpu.usage_percent),
                }}
              />
            </div>
          </div>

          <div className="metric-card">
            <div className="metric-header">
              <h3>RAM</h3>
              <span
                className="metric-value"
                style={{ color: getStatusColor(metrics.ram.usage_percent) }}
              >
                {metrics.ram.usage_percent}%
              </span>
            </div>
            <div className="metric-details">
              <p>
                {metrics.ram.used_gb.toFixed(1)} GB /{" "}
                {metrics.ram.total_gb.toFixed(1)} GB
              </p>
              <p>Available: {metrics.ram.available_gb.toFixed(1)} GB</p>
            </div>
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{
                  width: `${metrics.ram.usage_percent}%`,
                  backgroundColor: getStatusColor(metrics.ram.usage_percent),
                }}
              />
            </div>
          </div>

          <div className="metric-card">
            <div className="metric-header">
              <h3>Disk</h3>
              <span
                className="metric-value"
                style={{ color: getStatusColor(metrics.disk.usage_percent) }}
              >
                {metrics.disk.usage_percent}%
              </span>
            </div>
            <div className="metric-details">
              <p>
                {metrics.disk.used_gb.toFixed(1)} GB /{" "}
                {metrics.disk.total_gb.toFixed(1)} GB
              </p>
              <p>Free: {metrics.disk.free_gb.toFixed(1)} GB</p>
            </div>
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{
                  width: `${metrics.disk.usage_percent}%`,
                  backgroundColor: getStatusColor(metrics.disk.usage_percent),
                }}
              />
            </div>
          </div>

          {metrics.gpu && metrics.gpu.length > 0 && (
            <div className="metric-card">
              <div className="metric-header">
                <h3>GPU</h3>
                <span
                  className="metric-value"
                  style={{ color: getStatusColor(metrics.gpu[0].load_percent) }}
                >
                  {metrics.gpu[0].load_percent}%
                </span>
              </div>
              <div className="metric-details">
                <p>
                  Memory: {metrics.gpu[0].memory_used_gb.toFixed(2)} GB /{" "}
                  {metrics.gpu[0].memory_total_gb.toFixed(2)} GB
                </p>
                {metrics.gpu[0].temperature_c && (
                  <p>Temperature: {metrics.gpu[0].temperature_c}°C</p>
                )}
              </div>
              <div className="progress-bar">
                <div
                  className="progress-fill"
                  style={{
                    width: `${metrics.gpu[0].load_percent}%`,
                    backgroundColor: getStatusColor(
                      metrics.gpu[0].load_percent
                    ),
                  }}
                />
              </div>
            </div>
          )}

          {temp && (
            <div className="metric-card temperature">
              <div className="metric-header">
                <h3>Temperature</h3>
                <span className="metric-value">{temp.value.toFixed(1)}°C</span>
              </div>
              <div className="metric-details">
                <p>{temp.type} Temperature</p>
              </div>
            </div>
          )}
        </div>

        <div className="timestamp">
          Last Updated: {new Date(metrics.timestamp).toLocaleString()}
        </div>

        {history.length > 1 && (
          <div className="history">
            <h3>Recent Metrics</h3>
            <table>
              <thead>
                <tr>
                  <th>Time</th>
                  <th>CPU %</th>
                  <th>RAM %</th>
                  <th>Disk %</th>
                  {history[0].gpu && <th>GPU %</th>}
                </tr>
              </thead>
              <tbody>
                {history.slice(0, 5).map((item, idx) => (
                  <tr key={idx}>
                    <td>{new Date(item.timestamp).toLocaleTimeString()}</td>
                    <td>{item.cpu.usage_percent}%</td>
                    <td>{item.ram.usage_percent}%</td>
                    <td>{item.disk.usage_percent}%</td>
                    {item.gpu && <td>{item.gpu[0].load_percent}%</td>}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
